import React, { Component } from 'react'

export default class Contacts extends Component {
    render() {
        return (
            <div  className="container">
                <h1 _ngcontent-c53="" className="title">Контакти</h1>
                <div _ngcontent-c53="" className="content">
                    <div _ngcontent-c53="" className="subtitle">Залишились питання? Подзвоніть або напишіть нам,
                        проконсультуємо!)
                    </div>
                    <div _ngcontent-c53="" className="contacts">
                        <div _ngcontent-c53="" className="contacts__left">


                            <div _ngcontent-c53="" className="item ng-star-inserted">
                                <div _ngcontent-c53="" className="item__title">Безкоштовна гаряча лінія:</div>
                                <div _ngcontent-c53="" className="item__value">
                                    <a _ngcontent-c53="" className="value" href="tel:0800504004"><span
                                                                                                       className="clickable"><i
                                        className="icon phone"></i>0 800 504 004</span></a>
                                    <div  className="value"><span
                                                                                   className="clickable"><i
                                         className="icon callback"></i>Замовити зворотній дзвінок</span>
                                    </div>
                                </div>
                            </div>
                            <div _ngcontent-c53="" className="item ng-star-inserted">
                                <div _ngcontent-c53="" className="item__title">Наша електронна пошта:</div>
                                <div _ngcontent-c53="" className="item__value">
                                    <a _ngcontent-c53="" className="value" href="mailto:info@dressa.com.ua"><i
                                        _ngcontent-c53="" className="icon mail"></i>info@dressa.com.ua</a>
                                </div>
                            </div>
                            <div _ngcontent-c53="" className="item ng-star-inserted">
                                <div _ngcontent-c53="" className="item__title">Графік работи контакт-центру:</div>
                                <div _ngcontent-c53="" className="item__value">
                                    <div _ngcontent-c53="" className="value"><i _ngcontent-c53=""
                                                                                className="icon clock"></i>пн-пт: с 9:00
                                        до 18:00
                                    </div>
                                    <p _ngcontent-c53="" className="value">сб: с 9:00 до 17:00</p>
                                    <div _ngcontent-c53="" className="value"><i _ngcontent-c53=""
                                                                                className="icon time"></i>Прийняття замовлень
                                        24/7
                                    </div>
                                </div>
                            </div>



                        </div>
                        <div _ngcontent-c53="" className="contacts__right">


                            <div _ngcontent-c53="" className="item ng-star-inserted">
                                <div _ngcontent-c53="" className="item__title">Мессенджери і чат:</div>
                                <div _ngcontent-c53="" className="item__value">
                                    <a _ngcontent-c53="" className="value" href="viber://chat?number=%2B380684943721"><i
                                        _ngcontent-c53="" className="icon viber"></i>Ми у Viber</a>
                                    <a _ngcontent-c53="" className="value" href="tg://resolve?domain=DressaBot"><i
                                        _ngcontent-c53="" className="icon telegram"></i>Ми в Telegram</a>
                                    <div _ngcontent-c53="" className="value clickable"><i _ngcontent-c53=""
                                                                                          className="icon chat"></i>Онлайн-чат
                                    </div>
                                </div>
                            </div>
                            <div _ngcontent-c53="" className="item ng-star-inserted">
                                <div _ngcontent-c53="" className="item__title">Ми в соцмережах:</div>
                                <div _ngcontent-c53="" className="item__value">
                                    <a _ngcontent-c53="" className="value" href="https://www.facebook.com/dressa.ua/"><i
                                        _ngcontent-c53="" className="icon fb"></i>Facebook</a>
                                    <a _ngcontent-c53="" className="value"
                                       href="https://www.instagram.com/dressa.ua/"><i _ngcontent-c53=""
                                                                                      className="icon instagram"></i>Instagram</a>
                                </div>
                            </div>

                        </div>
                    </div>

                    <div _ngcontent-c53="" className="address">


                        <div _ngcontent-c53="" className="address__title ng-star-inserted">Наша адресса: м. Дрогобич, вул.
                            Завіжна 117
                        </div>

                        <div _ngcontent-c53="" className="address__map ng-star-inserted">
                            <iframe _ngcontent-c53="" allowFullScreen="" aria-hidden="false" frameBorder="0"
                                    height="500"
                                    src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2565.8274825014273!2d36.277750615575656!3d49.977093779412854!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x41270a7742aa4233%3A0x9d97cccb70a6f332!2z0YPQuy4g0J_Qu9C10YXQsNC90L7QstGB0LrQsNGPLCAxMTcsINCl0LDRgNGM0LrQvtCyLCDQpdCw0YDRjNC60L7QstGB0LrQsNGPINC-0LHQu9Cw0YHRgtGMLCA2MTAwMA!5e0!3m2!1sru!2sua!4v1589457966583!5m2!1sru!2sua"
                                    tabIndex="0" width="100%"></iframe>
                        </div>



                    </div>
                </div>
            </div>
        )
    }
}
